import React from "react";

class Weather extends React.Component {
  state = {
    place: "",
  };

  searchPlace = (e) => {
    e.preventDefault();
    if (this.state.place === "") {
      alert("ALl the fields are mandatory!");
      return;
    }
    this.props.addPlaceHandler(this.state);
    this.setState({ place: ""});
  };
  render() {
    return (
      <div className="ui main">
        <h2>Enter Place</h2>
        <form className="ui form" onSubmit={this.searchPlace}>
          <div className="field">
            <label>Place</label>
            <input
              type="text"
              name="place"
              placeholder="Place"
              value={this.state.place}
              onChange={(e) => this.setState({ place: e.target.value })}
            />
          </div>
          <button className="ui button blue">Search</button>
        </form>
      </div>
    );
  }
}

export default Weather;
